	public static String getClosetItem(String input, String type){
		
		// Choose the correct list to compare against
		ArrayList<String> itemlist = new ArrayList<String>();
		switch (type){
			case "species": 
				itemlist = new ArrayList<String>(Arrays.asList(species));
				break;
			case "move": 
				itemlist = new ArrayList<String>(Arrays.asList(moves));
				break;
			case "ability": 
				itemlist = new ArrayList<String>(Arrays.asList(abilities));
				break;
			case "item": 
				itemlist = new ArrayList<String>(Arrays.asList(items));
				break;
			case "nature": 
				itemlist = new ArrayList<String>(Arrays.asList(natures));
				break;
		}
		
		String bestmatch = new String();
		int bestdistance = 100; // arbitrary high value
		
		for (String item : itemlist){
			
			// Calculate the Levenshtein edit distance of the input from every species name/move/ability/nature/item
			int editdistance = distance(input,item);
			
			// Keep track of the item that has the best edit distance from the input
			if (bestdistance > editdistance) {
				bestdistance = editdistance;
				bestmatch = item;
			}
			//System.out.println(item + "\t" + editdistance + "\t\t\t" + "Current best distance: " + bestdistance);
		}
		
		return bestmatch;
		
	}
	
    public static int distance(String a, String b) {
        a = a.toLowerCase();
        b = b.toLowerCase();
        // i == 0
        int [] costs = new int [b.length() + 1];
        for (int j = 0; j < costs.length; j++)
            costs[j] = j;
        for (int i = 1; i <= a.length(); i++) {
            // j == 0; nw = lev(i - 1, j)
            costs[0] = i;
            int nw = i - 1;
            for (int j = 1; j <= b.length(); j++) {
                int cj = Math.min(1 + Math.min(costs[j], costs[j - 1]), a.charAt(i - 1) == b.charAt(j - 1) ? nw : nw + 1);
                nw = costs[j];
                costs[j] = cj;
            }
        }
        return costs[b.length()];
    }
 
